mapper.delete:
DELETE FROM CLOTHES_IMAGES
 WHERE CLOTHES_ID = #{clothesId}
   AND FILE_ID    = #{fileId}

mapper.insert:
INSERT INTO CLOTHES_IMAGES (
  CLOTHES_ID, FILE_ID, CREATED_AT, CREATED_BY
) VALUES (
  #{clothesId}, #{fileId,jdbcType=NUMERIC}, CURRENT_TIMESTAMP, #{createdBy}
)

mapper.select:
SELECT COUNT(*)
  FROM CLOTHES_IMAGES
 WHERE CLOTHES_ID = #{clothesId}

mapper.insert:
INSERT INTO CLOTHES_IMAGES (CLOTHES_ID, FILE_ID, CREATED_AT, CREATED_BY)

  SELECT #{l.clothesId}, #{l.fileId,jdbcType=NUMERIC}, CURRENT_TIMESTAMP, #{l.createdBy}
  FROM DUAL

mapper.delete:
DELETE FROM CLOTHES
WHERE CLOTHES_ID IN

  #{id}

DELETE FROM CLOTHES_IMAGES
WHERE CLOTHES_ID IN

  #{id}
