mapper.insert:
INSERT INTO users (
    user_id, email, password, nickname, file_id,
    login_type_code, kakao_id, gender_code, birth_date, is_active,
    created_at, created_by, logined_at
) VALUES (
    #{userId}, #{email}, #{password}, #{nickname}, #{fileId},
    #{loginTypeCode}, #{kakaoId}, #{genderCode}, #{birthDate}, #{isActive},
    CURRENT_TIMESTAMP, #{createdBy}, #{loginedAt}
)

mapper.update:
UPDATE users SET email = #{email}, password = #{password}, nickname = #{nickname}, file_id = #{fileId}, login_type_code = #{loginTypeCode},
kakao_id = #{kakaoId}, gender_code = #{genderCode}, birth_date = #{birthDate}, is_active = #{isActive}, updated_at = CURRENT_TIMESTAMP,
updated_by = #{updatedBy} WHERE user_id = #{userId}
